const MainApp = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // Determine currentView based on path
  let currentView = 'dashboard';
  if (location.pathname === '/' || location.pathname === '') {
    currentView = 'dashboard';
  } else if (location.pathname.startsWith('/materials/create')) {
    currentView = 'create';
  } else if (location.pathname.startsWith('/materials')) {
    currentView = 'list';
  } else if (location.pathname.startsWith('/vendors')) {
    currentView = 'vendor';
  }

  // Navigation button handler to change route
  const onViewChange = (view) => {
    switch(view) {
      case 'dashboard':
        navigate('/');
        break;
      case 'list':
        navigate('/materials');
        break;
      case 'create':
        navigate('/materials/create');
        break;
      case 'vendor':
        navigate('/vendors');
        break;
      default:
        navigate('/');
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <Navigation currentView={currentView} onViewChange={onViewChange} />
      <main className="py-6">
        <Outlet />
      </main>
      <Toaster />
    </div>
  );
};

function App() {
  const [showWelcome, setShowWelcome] = useState(true);

  if (showWelcome) {
    return <WelcomeScreen onGetStarted={() => setShowWelcome(false)} />;
  }

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<MainApp />}>
          <Route index element={<MaterialDashboard />} />
          <Route path="materials" element={<MaterialList />} />
          <Route path="materials/create" element={<MaterialForm />} />
          <Route path="vendors" element={<VendorDashboard />} />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
